const long long MAXN = 1e6 + 1;
using namespace std;

typedef long long ll;
typedef pair<int, int> pii;


long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}

#define int __int64_t

int used[MAXN];

int is_bipar = 0;

vector <int> g[MAXN];

void dfs(int v) {
    for (int i : g[v]) {
        int inverse = 3 - used[v];
        if (used[i]) {
            if (used[i] != inverse) {
                is_bipar = 0;
            }
            continue;
        }
        used[i] = inverse;
        dfs(i);
    }
}


main() {
	#ifdef IZI_KATKA
	assert(freopen("input", "r", stdin));
    assert(freopen("output", "w", stdout));
    #endif
    int n = readInt(), m = readInt();
    for (int i = 1; i <= m; i++) {
        int v = readInt(), u = readInt();
        g[v].pb(u);
        g[u].pb(v);
    }
    int isolated = 0;
    int bipartie = 0;
    int non_bipartie = 0;
    for (int i = 1; i <= n; i++) {
        if (used[i] != 0) continue;
        if (g[i].size() == 0) {
            isolated ++;
            continue;
        } else {
            used[i] = 1;
            is_bipar = 1;
            dfs(i);
            if (is_bipar) {
                bipartie ++;
            } else {
                non_bipartie++;
            }
        }
    }
    cout << 1ll * isolated * isolated + 2 * isolated * (n - isolated) + bipartie * bipartie + (bipartie + non_bipartie) * (bipartie + non_bipartie);
    return 0;
}

