const long long MAXN = 1e6 + 1;
using namespace std;

typedef long long ll;
typedef pair<int, int> pii;


long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}

int kek[MAXN];
vector <int> g[MAXN];

int p[MAXN];
int deg[MAXN];

set<pair <int, int> > s;

void dfs(int v, int par) {
    p[v] = par;
    deg[v] = deg[par] + 1;
    s.insert({-deg[v], v});
    for (int i : g[v]) {
        if (g[i].size() == 1) {
            kek[v] ++;
        }
        if (i == par) continue;
        dfs(i, v);
    }
}

int used[MAXN];
main() {
	#ifdef IZI_KATKA
	assert(freopen("input", "r", stdin));
    assert(freopen("output", "w", stdout));
    #endif
    int n = readInt();
    for (int i = 1; i < n; i++) {
        int v = readInt(), u = readInt();
        g[v].pb(u);
        g[u].pb(v);
    }
    dfs(1, 0);
    for (int i = 1; i <= n; i++) {
        if (kek[i] >= 2) {
            cout << "First";
            return 0;
        }
    }
    for (;!s.empty();) {
        pair <int, int> tmp = *s.begin();
        s.erase(s.begin());
        if (used[tmp.S]) continue;
        if (p[tmp.S] == 0 || used[p[tmp.S]]) {
            cout << "First";
            return 0;
        }
        int parent = p[tmp.S];
        used[tmp.S] = used[parent] = 1;
    }
    cout << "Second";
    return 0;
}
