int a,b;
char ch[105][105];

int main(){
	scanf("%d%d",&a,&b);
	printf("100 100\n");
	a--;
	b--;
	for(int i=0;i<50;i++){
		for(int j=0;j<100;j++){
			ch[i][j]='.';
		}
	}
	for(int i=50;i<100;i++){
		for(int j=0;j<100;j++){
			ch[i][j]='#';
		}
	}
	for(int i=0;i<b;i++){
		int x=i/50*2,y=i%50*2;
		ch[x][y]='#';
	}
	for(int i=0;i<a;i++){
		int x=99-i/50*2,y=i%50*2;
		ch[x][y]='.';
	}
	for(int i=0;i<100;i++){
		printf("%s\n",ch[i]);
	}
	return 0;
}
