#include <bits/stdc++.h>
using namespace std;


void solve(long long H, long long W, long long A, long long B){
    for (int i=0;i<H;i++){
        for (int j=0;j<W;j++){
            int ret = 0;
            if (i<B && j <A) ret = 1;
            if (i>=B && j>=A) ret=1;
            printf("%d",ret);
        }
        printf("\n");
    }

}

